problem.code:
class Book:
    def __init__(self, title, author):
        self.title=title
        self.author=author
        self.is_rented=False

    def __str__(self):
        return f'{self.title}'

class Student:
    def __init__(self, id, name):
        self.id=id
        self.name=name
        self.rented_books=[]

    def rent_book(self, book):
        if not book.is_rented:
            book.is_rented=True
            self.rented_books.append(book)
            return f'{book.title} 대여함.'
        else:
            return f'{book.title} 대여 중'

    def return_book(self, book):
        # 작성하세요
        pass

problem.solution:
def return_book(self, book):
    if book in self.rented_books:
        book.is_rented = False
        self.rented_books.remove(book)
        return f'{book.title} 반납함.'
    else:
        return f'{book.title} 대여하지 않음'

