problem.solution:
def f(k):
    for i in range(2, int(k**0.5)+1):
        if (k%i) == 0:  # 정답: (k%i) == 0 또는 k%i == 0
            return False
    return True

def f(k):
    for i in range(2, int(k**0.5)+1):
        if (k%i) == 0:
            return False
    return True

problem.code:
def f(k):
    for i in range(2, int(k**0.5)+1):
        if (k%i) == 0:
            return False
    return True

def f(k):
    cnt = 0
    for i in range(2, k):
        if (k%i) == 0:
            cnt += 1

    if cnt == 0:
        return True
    else:
        return False

