import math

class Point:
    def __init__(self, x, y):
        self.x = x
        self.y = y

    def distance(self, v):
        return math.sqrt((self.x - v.x)**2 + (self.y - v.y)**2)

    def quadrant(self):
        # 작성하세요
        pass

problem.solution:
def quadrant(self):
    if self.x > 0 and self.y > 0:
        return 1
    elif self.x < 0 and self.y > 0:
        return 2
    elif self.x < 0 and self.y < 0:
        return 3
    elif self.x > 0 and self.y < 0:
        return 4
    else:
        return 0
