problem.solution:
age = int(input())
license = input()
if age >= 18 and license == "yes":
    print("운전 가능")
else:
    print("운전 불가")

weekend = input()
holiday = input()
if weekend == "yes" or holiday == "yes":
    print("쉬는 날")
else:
    print("근무일")

rain = input()
if not rain == "yes":
    print("산책 좋은 날")
else:
    print("실내에서 휴식")

num = int(input())
if num >= 10 and num <= 20:
    print("범위 안")
else:
    print("범위 밖")

score = int(input())
attendance = int(input())
if score >= 90 or (score >= 80 and attendance >= 90):
    print("우수")
else:
    print("일반")

password = input()
has_digit = False
for char in password:
    if char.isdigit():
        has_digit = True
if len(password) >= 8 and has_digit:
    print("강한 비밀번호")
else:
    print("약한 비밀번호")

day = int(input())
if day == 6 or day == 7:
    print("휴일")
else:
    print("근무일")

my_list = [1, 2, 3]
if len(my_list) >= 3 and my_list[0] == 1:
    print("조건 만족")
else:
    print("조건 불만족")

text = input()
if not text == "" and not text == "test":
    print("유효한 입력")
else:
    print("무효한 입력")

age = int(input())
if (age >= 10 and age <= 19) or (age >= 20 and age <= 29):
    print("청년")
else:
    print("기타")

char = input()
if char.isalpha() or char.isdigit():
    print("유효한 문자")
else:
    print("특수문자")

password = input()
if len(password) >= 6 and password != "123456":
    print("사용 가능")
else:
    print("사용 불가")

month = int(input())
if (month == 3 or month == 4 or month == 5) or (month == 9 or month == 10 or month == 11):
    print("좋은 계절")
else:
    print("보통 계절")

numbers = [10, 50, 80]
valid = True
for num in numbers:
    if not (num >= 0 and num <= 100):
        valid = False
if valid:
    print("유효한 범위")
else:
    print("범위 초과")

name = input()
role = input()
if name == "admin" or role == "manager":
    print("접근 허용")
else:
    print("접근 거부")

hour = int(input())
if not (hour >= 9 and hour <= 18):
    print("업무 외 시간")
else:
    print("업무 시간")

filename = input()
if filename.endswith(".jpg") or filename.endswith(".png"):
    print("이미지 파일")
else:
    print("기타 파일")

text = input()
has_upper = False
for char in text:
    if char.isupper():
        has_upper = True
if len(text) >= 5 and has_upper:
    print("강한 텍스트")
else:
    print("약한 텍스트")

num = int(input())
if num % 3 == 0 or num % 5 == 0:
    print("특별한 수")
else:
    print("일반 수")

speed = int(input())
if not (speed >= 30 and speed <= 60):
    print("위험")
else:
    print("안전")
