char = input()
if char.isdigit():
    print("숫자")
elif char.isupper():
    print("대문자")
elif char.islower():
    print("소문자")
else:
    print("기타")

weight, height = input().split()
weight = float(weight)
height = float(height)
bmi = weight / (height * height)
if bmi >= 25:
    print("비만")
elif bmi >= 23:
    print("과체중")
elif bmi >= 18.5:
    print("정상")
else:
    print("저체중")

