day = int(input())
if day == 1:
    print("월요일")
elif day == 2:
    print("화요일")
elif day == 3:
    print("수요일")
elif day == 4:
    print("목요일")
elif day == 5:
    print("금요일")
elif day == 6 or day == 7:
    print("주말")
else:
    print("잘못된 입력")

amount = int(input())
if amount >= 100000:
    final = amount * 0.8
elif amount >= 50000:
    final = amount * 0.85
elif amount >= 20000:
    final = amount * 0.9
elif amount >= 10000:
    final = amount * 0.95
else:
    final = amount
print(f"최종 금액: {int(final)}원")

text = input()
if text.isdigit():
    print("숫자 문자열")
elif text.isalpha():
    print("알파벳 문자열")
elif text.isalnum():
    print("혼합 문자열")
else:
    print("특수 문자열")

