age, distance = input().split()
age = int(age)
distance = int(distance)
base_fee = distance * 100
if age <= 6:
    final_fee = 0
elif age <= 12:
    final_fee = int(base_fee * 0.7)
elif age <= 18:
    final_fee = int(base_fee * 0.8)
elif age >= 65:
    final_fee = int(base_fee * 0.5)
else:
    final_fee = base_fee
print(f"요금: {final_fee}원")

filename = input()
if filename.endswith(".jpg") or filename.endswith(".png") or filename.endswith(".gif"):
    print("이미지")
elif filename.endswith(".mp4") or filename.endswith(".avi") or filename.endswith(".mkv"):
    print("동영상")
elif filename.endswith(".txt") or filename.endswith(".doc") or filename.endswith(".pdf"):
    print("문서")
elif filename.endswith(".mp3") or filename.endswith(".wav"):
    print("음악")
else:
    print("기타")

