data = input().split(":")
name = data[0]
score = int(data[1])
if score >= 90:
    print(f"{name}님은 최우수입니다")
elif score >= 80:
    print(f"{name}님은 우수입니다")
elif score >= 70:
    print(f"{name}님은 보통입니다")
elif score >= 60:
    print(f"{name}님은 미흡합니다")
else:
    print(f"{name}님은 재시험이 필요합니다")
