problem.solution:
age = int(input())
if age >= 18:
    if age >= 65:
        print("경로우대")
    elif age >= 20:
        print("일반성인")
    else:
        print("제한성인")
else:
    if age >= 13:
        print("청소년")
    else:
        print("어린이")

score = int(input())
attendance = int(input())
if score >= 60:
    if attendance >= 20:
        print("합격")
    elif attendance >= 17:
        print("조건부합격")
    else:
        print("출석부족")
else:
    if attendance >= 22:
        print("재시험기회")
    else:
        print("불합격")

text = input()
if text.isalpha():
    if len(text) >= 5:
        print("긴알파벳")
    else:
        print("짧은알파벳")
else:
    if text.isdigit():
        print("숫자전용")
    else:
        print("혼합문자")

temp = int(input())
month = int(input())
summer = [6, 7, 8]
if month in summer:
    if temp >= 30:
        print("폭염")
    elif temp >= 25:
        print("더움")
    else:
        print("서늘")
else:
    if temp < 0:
        print("한파")
    elif temp < 10:
        print("추위")
    elif temp >= 20:
        print("따뜻함")
    else:
        print("쌀쌀")

num = int(input())
if num > 0:
    if num >= 100:
        if num % 2 == 0:
            print("큰짝수")
        else:
            print("큰홀수")
    else:
        if num % 2 == 0:
            print("작은짝수")
        else:
            print("작은홀수")
else:
    if num == 0:
        print("영")
    else:
        print("음수")

name = input()
score = int(input())
if len(name) >= 5:
    if score >= 80:
        print("골드회원")
    elif score >= 50:
        print("실버회원")
    else:
        print("브론즈회원")
else:
    if score >= 90:
        print("VIP")
    else:
        print("일반회원")

words = input().split()
if len(words) >= 3:
    all_alpha = True
    for word in words:
        if not word.isalpha():
            all_alpha = False
            break

    if all_alpha:
        print("완전한리스트")
    elif words[0].startswith('a'):
        print("a시작리스트")
    else:
        print("일반리스트")
else:
    if len(words) == 0:
        print("빈리스트")
    else:
        print("짧은리스트")

text = input()
if text[0].isupper():
    if text.endswith('n'):
        print("대문자N끝")
    elif text[-1].islower():
        print("대문자소문자끝")
    else:
        print("대문자기타끝")
else:
    if text[-1].isdigit():
        print("소문자숫자끝")
    else:
        print("소문자문자끝")

num1 = int(input())
num2 = int(input())
if num1 > num2:
    diff = num1 - num2
    if diff >= 10:
        print("큰차이")
    elif diff >= 5:
        print("보통차이")
    else:
        print("작은차이")
else:
    if num1 == num2:
        print("동일")
    else:
        print("역전")

name = input()
age = int(input())
score = int(input())
if age >= 20:
    if name.isalpha() and score >= 80:
        print("프리미엄")
    elif score >= 60:
        print("스탠다드")
    else:
        print("베이직")
else:
    if len(name) >= 5 and score >= 90:
        print("주니어플러스")
    elif score >= 70:
        print("주니어")
    else:
        print("키즈")
