score = int(input())
if score >= 90:
    print("A")
elif score >= 80:
    print("B")
elif score >= 70:
    print("C")
elif score >= 60:
    print("D")
else:
    print("F")

age = int(input())
if age >= 65:
    print("노인")
elif age >= 20:
    print("성인")
elif age >= 13:
    print("청소년")
else:
    print("어린이")

temp = int(input())
if temp >= 30:
    print("더움")
elif temp >= 20:
    print("따뜻함")
elif temp >= 10:
    print("선선함")
elif temp >= 0:
    print("추움")
else:
    print("매우 추움")

num = int(input())
if num > 0 and num % 2 == 0:
    print("양의 짝수")
elif num > 0 and num % 2 == 1:
    print("양의 홀수")
elif num == 0:
    print("영")
else:
    print("음수")

text = input()
if len(text) >= 10:
    print("긴 문자열")
elif len(text) >= 5:
    print("보통 문자열")
elif len(text) >= 1:
    print("짧은 문자열")
else:
    print("빈 문자열")

text = input()
if text.isdigit():
    print("숫자")
elif text.isalpha() and text.isupper():
    print("대문자")
elif text.isalpha() and text.islower():
    print("소문자")
elif text.isalpha():
    print("알파벳")
else:
    print("혼합")

weight = float(input())
height = float(input())
bmi = weight / (height * height)
if bmi >= 30:
    print("비만")
elif bmi >= 25:
    print("과체중")
elif bmi >= 18.5:
    print("정상")
else:
    print("저체중")

month = int(input())
if month in [12, 1, 2]:
    print("겨울")
elif month in [3, 4, 5]:
    print("봄")
elif month in [6, 7, 8]:
    print("여름")
elif month in [9, 10, 11]:
    print("가을")
else:
    print("잘못된 월")

text = input()
if text.startswith('p') and text.endswith('n'):
    print("python형")
elif text.startswith('p'):
    print("p시작")
elif text.endswith('n'):
    print("n끝")
else:
    print("기타")

