text = input()
if len(text.strip()) == 0 and len(text) > 0:
    print("공백만")
elif ' ' in text:
    print("공백 포함")
elif len(text) == 0:
    print("빈 문자열")
else:
    print("공백 없음")

num = int(input())
if num % 15 == 0:
    print("15배수")
elif num % 5 == 0:
    print("5배수")
elif num % 3 == 0:
    print("3배수")
else:
    print("해당 없음")

words = input().split()
if len(words) >= 5:
    print("많음")
elif len(words) >= 3:
    print("보통")
elif len(words) >= 1:
    print("적음")
else:
    print("없음")

text = input()
if text.isalnum() and not text.isalpha() and not text.isdigit():
    print("영숫자")
elif text.isalpha():
    print("문자")
elif text.isdigit():
    print("숫자")
else:
    print("특수문자 포함")

score1 = int(input())
score2 = int(input())
if score1 >= 90 and score2 >= 90:
    print("우수")
elif score1 >= 70 and score2 >= 70:
    print("양호")
elif score1 >= 60 or score2 >= 60:
    print("보통")
else:
    print("미흡")
