problem.solution:
colors = ['빨강', '파랑', '노랑', '초록']
numbers = [1, 2, 3, 4]
for i, (color, number) in enumerate(zip(colors, numbers)):
    if i % 2 == 0:
        print(f"인덱스 {i}: {color}-{number}")

months = ['1월', '2월', '3월']
income = [300, 350, 280]
expense = [250, 300, 290]
for i, (month, inc, exp) in enumerate(zip(months, income, expense)):
    profit = inc - exp
    if profit >= 0:
        result = f"이익 {profit}"
    else:
        result = f"손실 {-profit}"
    print(f"{i+1}번째 {month}: 수입 {inc}, 지출 {exp}, {result}")

fruits = ['사과', '바나나', '오렌지', '포도']
prices = [1000, 1500, 2000, 2500]
for i, (fruit, price) in enumerate(zip(fruits, prices)):
    if i == 0:
        print(f"첫 번째: {fruit}({price}원)")
    if i == len(fruits) - 1:
        print(f"마지막: {fruit}({price}원)")
