problem.solution:
while True:
    score = int(input())
    if score == -1:
        print("점수 입력 종료")
        break
    if score == 0:
        continue
    print(f"유효한 점수: {score}")

while True:
    print("1. 게임 시작")
    print("2. 설정")
    print("3. 종료")

    choice = input()

    if choice == "1":
        print("게임을 시작합니다!")
    elif choice == "2":
        print("설정 메뉴입니다")
    elif choice == "3":
        print("게임을 종료합니다")
        break
    else:
        continue  # 잘못된 입력은 무시

while True:
    num1_input = input("첫 번째 숫자: " if 'num1_input' not in locals() else "")
    print(f"첫 번째 숫자: {num1_input}")
    if num1_input == "quit":
        print("계산기를 종료합니다")
        break

    num1 = int(num1_input)
    operator = input("연산자: " if 'operator' not in locals() else "")
    print(f"연산자: {operator}")
    num2 = int(input("두 번째 숫자: " if 'num2' not in locals() else ""))
    print(f"두 번째 숫자: {num2}")

    if operator == "/" and num2 == 0:
        print("0으로 나눌 수 없습니다")
        continue

    if operator == "+":
        result = num1 + num2
    elif operator == "-":
        result = num1 - num2
    elif operator == "*":
        result = num1 * num2
    elif operator == "/":
        result = num1 / num2

    print(f"결과: {int(result) if result == int(result) else result}")

while True:
    password = input()
    if len(password) < 4:
        print("비밀번호가 너무 짧습니다")
        continue
    if password == "1234":
        print("로그인 성공!")
        break
    else:
        print("비밀번호가 틀렸습니다")

while True:
    print("=== 게임 메뉴 ===")
    print("1. 새 게임")
    print("2. 불러오기")
    print("3. 설정")
    print("4. 종료")

    choice = input()

    if choice == "1":
        print("새 게임을 시작합니다")
    elif choice == "2":
        print("게임을 불러옵니다")
    elif choice == "3":
        print("설정 화면입니다")
    elif choice == "4":
        print("게임을 종료합니다")
        break
    else:
        continue  # 잘못된 입력은 무시
