problem.code:
class Movie:
    def __init__(self, title, seats):
        self.title = title
        self.seats = seats

    def reserve_seat(self):
        # 작성하세요
        pass

problem.solution:
def reserve_seat(self):
    if self.seats > 0:
        self.seats -= 1
        return True
    else:
        return False
