problem.code:
def grape_recursive(a, b):
    if b == 0:
        return a
    return grape_recursive(b, a % b)

def grape(a, b):
    # 여기에 코드를 작성하세요
    pass

problem.solution:
def grape(a, b):
    while b != 0:
        temp = b
        b = a % b
        a = temp
    return a

problem.code:
def honeydew_recursive(a, b):
    if b == 0:
        return a
    return honeydew_recursive(b, a % b)

def honeydew_lcm_recursive(a, b):
    return (a * b) // honeydew_recursive(a, b)

def honeydew(a, b):
    # 여기에 코드를 작성하세요
    pass

problem.solution:
def honeydew(a, b):
    temp_a, temp_b = a, b
    while temp_b != 0:
        temp_a, temp_b = temp_b, temp_a % temp_b
    gcd_result = temp_a

    return (a * b) // gcd_result

problem.code:
def kiwi_recursive(n):
    if n == 0:
        return "0"
    if n == 1:
        return "1"
    return kiwi_recursive(n // 2) + str(n % 2)

def kiwi(n):
    # 여기에 코드를 작성하세요
    pass

problem.solution:
def kiwi(n):
    if n == 0:
        return "0"

    result = ""
    while n > 0:
        result = str(n % 2) + result
        n = n // 2
    return result

problem.code:
def lemon_recursive(n, base):
    if n == 0:
        return "0"
    if n < base:
        if n < 10:
            return str(n)
        else:
            return chr(ord('A') + n - 10)

    remainder = n % base
    if remainder < 10:
        remainder_char = str(remainder)
    else:
        remainder_char = chr(ord('A') + remainder - 10)

    return lemon_recursive(n // base, base) + remainder_char

def lemon(n, base):
    # 여기에 코드를 작성하세요
    pass

problem.solution:
def lemon(n, base):
    if n == 0:
        return "0"

    result = ""
    while n > 0:
        remainder = n % base
        if remainder < 10:
            result = str(remainder) + result
        else:
            result = chr(ord('A') + remainder - 10) + result
        n = n // base
    return result
