def factorial(n):
    # 여기에 코드를 작성하세요
    pass

problem.solution:
def factorial(n):
    # 기저 조건: n이 0 또는 1이면 1 반환
    if n <= 1:
        return 1
    # 재귀 호출: n * (n-1)!
    return n * factorial(n - 1)

problem.code:
def power(a, n):
    # 여기에 코드를 작성하세요
    pass

problem.solution:
def power(a, n):
    # 기저 조건: a^0 = 1
    if n == 0:
        return 1
    # 재귀 호출: a * a^(n-1)
    return a * power(a, n - 1)

problem.code:
def sum_to_n(n):
    # 여기에 코드를 작성하세요
    pass

problem.solution:
def sum_to_n(n):
    # 기저 조건: n이 0 이하이면 0 반환
    if n <= 0:
        return 0
    # 재귀 호출: n + (1부터 n-1까지의 합)
    return n + sum_to_n(n - 1)

problem.code:
def sum_of_squares(n):
    # 여기에 코드를 작성하세요
    pass

problem.solution:
def sum_of_squares(n):
    # 기저 조건: n이 0 이하이면 0 반환
    if n <= 0:
        return 0
    # 재귀 호출: n² + (1²부터 (n-1)²까지의 합)
    return n * n + sum_of_squares(n - 1)

problem.code:
def sum_of_digits(n):
    # 여기에 코드를 작성하세요
    pass

problem.solution:
def sum_of_digits(n):
    # 기저 조건: n이 0이면 0 반환
    if n == 0:
        return 0
    # 재귀 호출: 마지막 자릿수 + 나머지 자릿수들의 합
    return (n % 10) + sum_of_digits(n // 10)

problem.code:
def product_of_digits(n):
    # 여기에 코드를 작성하세요
    pass

problem.solution:
def product_of_digits(n):
    # 기저 조건: n이 0이면 1 반환 (곱셈의 항등원)
    if n == 0:
        return 1
    # 재귀 호출: 마지막 자릿수 × 나머지 자릿수들의 곱
    return (n % 10) * product_of_digits(n // 10)

