def count_digits(n):
    # 여기에 코드를 작성하세요
    pass

problem.solution:
def count_digits(n):
    # 기저 조건: 한 자릿수이면 1 반환
    if n < 10:
        return 1
    # 재귀 호출: 1 + (마지막 자릿수를 제거한 숫자의 자릿수)
    return 1 + count_digits(n // 10)

problem.code:
def string_length(s):
    # 여기에 코드를 작성하세요
    pass

problem.solution:
def string_length(s):
    # 기저 조건: 빈 문자열이면 0 반환
    if not s:  # s == "" 와 같음
        return 0
    # 재귀 호출: 1 + (첫 글자를 제거한 문자열의 길이)
    return 1 + string_length(s[1:])

problem.code:
def count_char(s, c):
    # 여기에 코드를 작성하세요
    pass

problem.solution:
def count_char(s, c):
    # 기저 조건: 빈 문자열이면 0 반환
    if not s:
        return 0
    # 첫 글자가 찾는 문자와 같은지 확인
    if s[0] == c:
        # 같으면 1을 더하고 나머지 문자열 확인
        return 1 + count_char(s[1:], c)
    else:
        # 다르면 0을 더하고 나머지 문자열 확인
        return 0 + count_char(s[1:], c)

