def count_vowels(s):
    # 여기에 코드를 작성하세요
    pass

problem.solution:
def count_vowels(s):
    # 기저 조건: 빈 문자열이면 0 반환
    if not s:
        return 0

    # 모음 목록
    vowels = "aeiouAEIOU"  # 대소문자 모두 고려

    # 첫 글자가 모음인지 확인
    if s[0] in vowels:
        # 모음이면 1을 더하고 나머지 문자열 확인
        return 1 + count_vowels(s[1:])
    else:
        # 모음이 아니면 0을 더하고 나머지 문자열 확인
        return 0 + count_vowels(s[1:])

problem.code:
def array_sum(arr):
    # 여기에 코드를 작성하세요
    pass

problem.solution:
def array_sum(arr):
    # 기저 조건: 빈 리스트이면 0 반환
    if not arr:
        return 0
    # 재귀 호출: 첫 번째 원소 + 나머지 원소들의 합
    return arr[0] + array_sum(arr[1:])

problem.code:
def array_product(arr):
    # 여기에 코드를 작성하세요
    pass

problem.solution:
def array_product(arr):
    # 기저 조건: 빈 리스트이면 1 반환 (곱셈의 항등원)
    if not arr:
        return 1
    # 재귀 호출: 첫 번째 원소 × 나머지 원소들의 곱
    return arr[0] * array_product(arr[1:])

